import { defineStore } from "pinia";
import axios from "axios";
export default defineStore('main', {
    state: () => {
        return {
            count: 10,
            list: [{
                name: 'iphone',
                price: 1000
            }],
            timuList: []
        }
    },
    getters: {
        sumPrice: (state) => {
            return state.list.reduce((pre, item) => {
                return pre + (item.price)
            }, 0)
        }
    },
    actions: {
        async getTimu() {
            let result = axios.get('/timu.json')
            this.timuList = (await result).data
        }
    }
})
