import Home from '@/pages/Home'
import Detail from '@/pages/Detail'
import Login from '@/pages/Login'
import Register from '@/pages/Register'
import AddCartSuccess from '@/pages/AddCartSuccess'
import ShopCart from '@/pages/ShopCart'
import Trade from '@/pages/Trade'
import Pay from '@/pages/Pay'
import PaySuccess from '@/pages/PaySuccess'
import Center from '@/pages/Center'
import MyOrder from '@/pages/Center/MyOrder'
import GroupOrder from '@/pages/Center/GroupOrder'
export default [
    {
        path: '/',
        redirect: '/home',
    },
    {
        name: 'home',
        path: '/home',
        component: Home,
        meta: {
            isShow: true,
            title: '活力GO! 年轻人的购物平台',
            needLogin: false,
        },
    },
    {
        name: 'search',
        path: '/search/:keyword?',
        component: () => import('@/pages/Search'),
        props: true,
        meta: {
            isShow: true,
            title: '活力GO! 商品搜索',
            needLogin: false,
        },
    },
    {
        name: 'login',
        path: '/login',
        component: Login,
        meta: {
            isShow: false,
            title: '活力GO! 登录',
            needLogin: false,
        },
    },
    {
        name: 'register',
        path: '/register',
        component: Register,
        meta: {
            isShow: false,
            title: '活力GO! 新用户注册',
            needLogin: false,
        },
    },
    {
        name: 'detail',
        path: '/detail/:skuId',
        component: Detail,
        meta: {
            isShow: true,
            title: '活力GO! 商品详情',
            needLogin: false,
        },
    },
    {
        name: 'AddCartSuccess',
        path: '/addCartSuccess',
        component: AddCartSuccess,
        meta: {
            isShow: true,
            title: '活力GO! 成功添加到购物车',
            needLogin: false,
        },
    },
    {
        name: 'ShopCart',
        path: '/shopCart',
        component: ShopCart,
        meta: {
            isShow: true,
            title: '活力GO! 我的购物车',
            needLogin: false,
        },
    },
    {
        name: 'Trade',
        path: '/trade',
        component: Trade,
        meta: {
            isShow: true,
            title: '活力GO! 结算页',
            needLogin: true,
        },
        boforeEnter(to, from, next) {
            if (from.path === '/shopCart') {
                next()
            } else {
                next(false)
            }
        }
    },
    {
        name: 'Pay',
        path: '/pay',
        component: Pay,
        meta: {
            isShow: true,
            title: '活力GO! 在线支付',
            needLogin: true,
        },
        boforeEnter(to, from, next) {
            if (from.path === '/trade') {
                next()
            } else {
                next(false)
            }
        }
    },
    {
        name: 'PaySuccess',
        path: '/paySuccess',
        component: PaySuccess,
        meta: {
            isShow: true,
            title: '活力GO! 支付成功页',
            needLogin: true,
        },
        boforeEnter(to, from, next) {
            if (from.path === '/pay') {
                next()
            } else {
                next(false)
            }
        }
    },
    {
        name: 'Center',
        path: '/center',
        component: Center,
        meta: {
            isShow: true,
            title: '活力GO! 个人中心',
            needLogin: true,
        },
        children: [
            {
                path: 'myorder',
                component: MyOrder,
                meta: {
                    needLogin: true,
                },
            },
            {
                path: 'groupOrder',
                component: GroupOrder,
                meta: {
                    needLogin: true,
                },
            },
            {
                path: '/center',
                redirect: '/center/myorder',
                meta: {
                    needLogin: true,
                },
            }
        ]
    }
]
