import Home from '@/pages/Home'
import Detail from '@/pages/Detail'
import Login from '@/pages/Login'
import Register from '@/pages/Register'

export default [
    {
        path: '/',
        redirect: '/home'
    },
    {
        name: 'home',
        path: '/home',
        component: Home,
        meta: { isShow: true },
    },
    {
        name: 'search',
        path: '/search/:keyword?',
        component: () => import('@/pages/Search'),
        props: true,
        meta: { isShow: true },
    },
    {
        name: 'login',
        path: '/login',
        component: Login,
        meta: { isShow: false },
    },
    {
        name: 'register',
        path: '/register',
        component: Register,
        meta: { isShow: false },
    },
    {
        name: 'detail',
        path: '/detail/:skuId',
        component: Detail,
        meta: { isShow: true },
    }
]
