import axios from "axios";
const requests = axios.create({
    baseURL: '/api',
    timeout: 5000
})
requests.interceptors.request.use(
    config => config,
    err => { Promise.reject(err) }
)
requests.interceptors.response.use(
    res => res.data,
    err => Promise.reject(new Error('faile'))
)
export default requests
