import { defineStore } from "pinia";
import { reqGetCartList } from "@/api";
export default defineStore('shopcart', {
    state: () => {
        return {
            cartList: []
        }
    },
    getters: {
        cartInfoList() {
            let res = this.cartList[0] !== undefined ? this.cartList[0].cartInfoList : []
            return res
        }
    },
    actions: {
        getCartList() {
            reqGetCartList().then(
                res => {
                    this.cartList = res.data
                }
            )
        }
    }
})
