import { defineStore } from "pinia";
import { reqAddressInfo, reqOrderInfo, reqSubmitOrder, reqPayInfo } from "@/api";
export default defineStore('trade', {
    state: () => {
        return {
            address: [],
            OrderInfo: {},
            orderId: '',
            payInfo: {}
        }
    },
    getters: {

    },
    actions: {
        getAddress() {
            reqAddressInfo().then(
                res => this.address = res.data
            )
        },
        getOrderInfo() {
            reqOrderInfo().then(
                res => this.OrderInfo = res.data
            )
        },
        async submitOrder(tradeNo, data) {
            let result = await reqSubmitOrder(tradeNo, data)
            if (result.code == 200) {
                this.orderId = result.data
                return 'ok'
            } else {
                return Promise.reject(new Error(result.message))
            }
        },
        getPayInfo(orderId) {
            reqPayInfo(orderId).then(
                res => this.payInfo = res.data
            )
        }
    }
})
