import { createWebHistory, createRouter } from 'vue-router'
import { routes } from './routes'
const history = createWebHistory();
const router = createRouter({
    history,
    routes,
    scrollBehavior(to, from, savedPosition) {
        // 始终滚动到顶部
        return { top: 0 }
    },
})
export default router
