import Home from '@/pages/Home'
import Detail from '@/pages/Detail'
import Login from '@/pages/Login'
import Register from '@/pages/Register'
import AddCartSuccess from '@/pages/AddCartSuccess'
import ShopCart from '@/pages/ShopCart'
import useUserStore from '@/store/User'
export default [
    {
        path: '/',
        redirect: '/home',
        beforeEnter(to, from, next) {
            const userStore = useUserStore()
            userStore.getUserInfo()
            next()
        }
    },
    {
        name: 'home',
        path: '/home',
        component: Home,
        meta: {
            isShow: true,
            title: '活力GO! 年轻人的购物平台'
        },
    },
    {
        name: 'search',
        path: '/search/:keyword?',
        component: () => import('@/pages/Search'),
        props: true,
        meta: {
            isShow: true,
            title: '活力GO! 商品搜索'
        },
    },
    {
        name: 'login',
        path: '/login',
        component: Login,
        meta: {
            isShow: false,
            title: '活力GO! 登录'
        },
    },
    {
        name: 'register',
        path: '/register',
        component: Register,
        meta: {
            isShow: false,
            title: '活力GO! 新用户注册'
        },
    },
    {
        name: 'detail',
        path: '/detail/:skuId',
        component: Detail,
        meta: {
            isShow: true,
            title: '活力GO! 商品详情'
        },
    },
    {
        name: 'AddCartSuccess',
        path: '/addCartSuccess',
        component: AddCartSuccess,
        meta: {
            isShow: true,
            title: '活力GO! 成功添加到购物车'
        },
    },
    {
        name: 'ShopCart',
        path: '/shopCart',
        component: ShopCart,
        meta: {
            isShow: true,
            title: '活力GO! 我的购物车'
        },
    }
]
