import ListContainer from './ListContainer';
import Recommend from './Recommend';
import Rank from './Rank';
import Like from './Like';
import Floor from './Floor';
import Brand from './Brand/';
import useMainStore from '@/store/index';
import { storeToRefs } from 'pinia';
export default {
  name: 'index',

  setup(__props, {
    expose
  }) {
    expose();
    const store = useMainStore();
    let {
      count
    } = storeToRefs(store);

    function handleClick() {
      // 方法一state修改方式
      count.value++;
    }

    function patchClick() {
      // 方法二 $patch修改
      store.list.push({
        name: 'xiaomi',
        price: 2000
      });
      store.$patch({
        count: store.count + 5,
        list: store.list
      });
    }

    function patchFnClick() {
      // 方法三函数操作
      store.$patch(state => {
        state.count += 5;
        state.list.push({
          name: 'oppo',
          price: 3000
        });
      });
    }

    function toggleState() {
      store.$state = {
        count: 100,
        list: [{
          name: 'vivo',
          price: 2100
        }]
      };
    }

    const __returned__ = {
      store,
      count,
      handleClick,
      patchClick,
      patchFnClick,
      toggleState,
      ListContainer,
      Recommend,
      Rank,
      Like,
      Floor,
      Brand,
      useMainStore,
      storeToRefs
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }

};
