import { defineStore } from "pinia";
import { reqGetDetailInfo } from "@/api";
export default defineStore({
    state: () => {
        return {
            detailInfo: []
        }
    },
    getters: {

    },
    actions: {
        getDetailInfo(params = []) {
            reqGetDetailInfo(params).then(
                res => this.detailInfo = res.data
            )
        }
    }
})
