import { defineStore } from "pinia";
import { reqGetCode } from "@/api";
export default defineStore('user', {
    state: () => {
        return {
            code: ''
        }
    },
    getters: {

    },
    actions: {
        getCode() {
            reqGetCode().then(
                res => this.code = res.data
            )
        }
    }
})
