import { defineStore } from "pinia";
import { reqGetCartList } from "@/api";
export default defineStore('shopcart', {
    state: () => {
        return {
            cartList: []
        }
    },
    getters: {
        cartInfoList() {
            if (this.cartList) {
                return this.cartList[0].cartInfoList || []
            }
            return []
        }
    },
    actions: {
        getCartList() {
            reqGetCartList().then(
                res => {
                    this.cartList = res.data
                }
            )
        }
    }
})
