import { defineStore } from "pinia";
import { reqAddressInfo, reqOrderInfo } from "@/api";
export default defineStore('trade', {
    state: () => {
        return {
            address: [],
            OrderInfo: {},
        }
    },
    getters: {

    },
    actions: {
        getAddress() {
            reqAddressInfo().then(
                res => this.address = res.data
            )
        },
        getOrderInfo() {
            reqOrderInfo().then(
                res => this.OrderInfo = res.data
            )
        },
        async submitOrder(tradeNo, data) {
            let result = await reqOrderInfo(tradeNo, data)
            if (result.code == 200) {
                return 'ok'
            } else {
                return Promise.reject(new ErrorEvent(result.message))
            }
        }
    }
})
