import { defineStore } from "pinia";
import axios from "axios";
export default defineStore('main', {
    state: () => {
        return {
            count: 10,
            list: [{
                name: 'iphone',
                price: 1000
            }],
            timuList: []
        }
    },
    getters: {
        sumPrice: (state) => {
            return state.list.reduce((pre, item) => {
                return pre + (item.price)
            }, 0)
        }
    },
    actions: {
        getTimu() {
            axios.get('/timu.json').then(res => {
                this.timuList = res.data
            })

        }
    }
})
