import { defineStore } from "pinia";
import { reqGetCode } from "@/api";
export default defineStore('user', {
    state: () => {
        return {
            code: ''
        }
    },
    getters: {

    },
    actions: {
        getCode(phone) {
            reqGetCode(phone).then(
                res => this.code = res.data
            )
        }
    }
})
