import { defineStore } from "pinia";
import { reqGetSearchInfo } from "@/api";
export default defineStore('search', {
    state: () => {
        return {
            SearchInfo: []
        }
    },
    getters: {

    },
    actions: {
        getSearchInfo(params) {
            reqGetSearchInfo(params).then(
                res => this.SearchInfo = res.data
            )
        }
    }
})
