import requests from "./request";
import mockRequest from './mockServe'
export const reqCategoryList = () => requests({ url: '/product/getBaseCategoryList', method: 'get' })
export const reqGetBannerList = () => mockRequest({ url: "/banner", method: 'get' })
export const reqGetFloorList = () => mockRequest({ url: '/floor', method: 'get' })
export const reqGetSearchInfo = (params) => requests({ url: '/list', method: 'post', data: params })
export const reqGetDetailInfo = (skuId) => requests({ url: `/item/${skuId}`, method: 'get' })
export const reqAddOrUpdateShopCar = (skuId, skuNum) => requests({ url: `/cart/addToCart/${skuId}/${skuNum}`, method: 'post' })
export const reqGetShopcart = () => requests({ url: '/cart/cartList', method: 'get' })
