import { defineStore } from "pinia";
import { reqGetDetailInfo } from "@/api";
export default defineStore('detail', {
    state: () => {
        return {
            detailInfo: {}
        }
    },
    getters: {

    },
    actions: {
        getDetailInfo(params = 1) {
            reqGetDetailInfo(params).then(
                res => this.detailInfo = res.data
            )
        }
    }
})
